<form name="form" id="voucher_form">	<fieldset>		<ul class="list_form">			<li>				<textarea name="cgv" rows="8" cols="80"><?=$txt?></textarea>			</li>		</ul>		<ul class="list_action">			<li>				<input type="button" class="button btn_L btn_annul" value="Annuler" onClick="_inPop.close(this)" />			</li>			<li>				<input class="button btnBlue" type="button" name="submit" id="_submit_cgv_admin_form" value="Valider" />			</li>		</ul>		<input type="hidden" name="id" id="cgv_id" value="<?=$id?>">	</fieldset></form>